import { makeObservable, observable, action } from "mobx"
import { observer } from "mobx-react";

class ModalStore {
  rootStore;
  _IsModalOpen = false;

  constructor(root) {
    makeObservable(this, {
      _IsModalOpen: observable,
      openModal: action,
      closeModal: action
    });
    
    //다른 store의 데이터를 이용할 여지
    this.rootStore = root;

    this._IsModalOpen = _IsModalOpen
  }

  openModal(){
    console.log("왜")
    this._IsModalOpen = true;
  }
  closeModal(){
    this._IsModalOpen = false;
  }
}
export default new ModalStore();
