import { makeObservable, observable, action } from "mobx"

export default class ModalStore {
  rootStore;
  isModalOpen = "false";

  constructor() {
    makeObservable(this, {
      isModalOpen: observable,
      openModal: action,
      closeModal: action
    });
  }

  openModal(){
    console.log("모달이 열립니다.")
    this._IsModalOpen = true;
  }
  closeModal(){
    console.log("모달이 닫힙니다.")
    this._IsModalOpen = false;
  }
}
